import React, { useContext, useState } from 'react';
import { Route, Redirect } from 'react-router-dom';
import { IonApp, IonRouterOutlet, IonMenu, IonHeader, IonToolbar, IonTitle, IonContent, IonList, IonItem, IonIcon, IonLabel, IonMenuToggle } from '@ionic/react';
import { IonReactRouter } from '@ionic/react-router';

/* Core CSS required for Ionic components to work properly */
import '@ionic/react/css/core.css';

/* Basic CSS for apps built with Ionic */
import '@ionic/react/css/normalize.css';
import '@ionic/react/css/structure.css';
import '@ionic/react/css/typography.css';

/* Optional CSS utils that can be commented out */
import '@ionic/react/css/padding.css';
import '@ionic/react/css/float-elements.css';
import '@ionic/react/css/text-alignment.css';
import '@ionic/react/css/text-transformation.css';
import '@ionic/react/css/flex-utils.css';
import '@ionic/react/css/display.css';

/* Theme variables */
import './theme/variables.css';
import AllActivities from './pages/AllActivities/AllActivities';
import AddActivity from './pages/AddActivity/AddActivity';
import Alumnes from './pages/Alumnes/Alumnes';
import Moduls from './pages/Moduls/Moduls';
import Login from './pages/Login/Login';
import { bodyOutline, newspaperOutline } from 'ionicons/icons';
import ContextProvider from './data/ContextProvider';
import Context from './data/context';
import * as $ from 'jquery' 

const App: React.FC = () => {
    const context = useContext(Context);

    var item = window.localStorage.getItem("JWT");
    context.jwt = (item ?? '');
        var res = $.ajax({
            method: "POST",
            url: "http://192.168.2.212/ProjecteGit/JWT/validate_token.php",
            data: { jwt: context.jwt },
            async: false
        }).responseText;
        debugger;
        if (res != null && res != undefined){
        var resJSON = JSON.parse(res);
            // .done(function(res: Validar) {
            if (resJSON?.message == "Access granted." && resJSON?.data != null){
                //activitiesContext.jwt = res.jwt;
                context.logged = true;
            } else {
                context.logged = false;
            }
        } else {
            context.logged = false;
        }

    var rutes = context.logged ? [ {id: "/alumnes", comp: "Alumnes" }, { id: "/moduls", comp: "Moduls" }, { id: "/logout", comp: "Logout" }] : [{id: "/login", comp: "Login"}];
    return (
    <IonApp>
        <IonReactRouter>
            <IonMenu contentId='scheduleAppM1'>
                <IonHeader>
                    <IonToolbar>
                        <IonTitle>Projecte</IonTitle>
                    </IonToolbar>
                </IonHeader>
                <IonContent>
                    <IonList>
                        {/* <IonMenuToggle>
                            <IonItem routerLink="/all-activities" routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={bodyOutline}/>
                                <IonLabel>All activities</IonLabel>
                            </IonItem>
                        </IonMenuToggle>
                        <IonMenuToggle>
                            <IonItem routerLink="/add-activity" routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={newspaperOutline}/>
                                <IonLabel>Add activity</IonLabel>
                            </IonItem>
                        </IonMenuToggle>
                        <IonMenuToggle>
                            <IonItem routerLink="/login" routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={bodyOutline}/>
                                <IonLabel>Login</IonLabel>
                            </IonItem>
                        </IonMenuToggle>
                        <IonMenuToggle>
                            <IonItem routerLink="/alumnes" routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={bodyOutline}/>
                                <IonLabel>Alumnes</IonLabel>
                            </IonItem>
                        </IonMenuToggle>
                        <IonMenuToggle>
                            <IonItem routerLink="/moduls" routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={bodyOutline}/>
                                <IonLabel>Moduls</IonLabel>
                            </IonItem>
                        </IonMenuToggle> */}
                        { rutes.map(ruta => (
                            <IonMenuToggle>
                            <IonItem routerLink={ruta.id} routerDirection="none" lines="none">
                                <IonIcon color="medium" slot="start" icon={bodyOutline}/>
                                <IonLabel>{ruta.comp}</IonLabel>
                            </IonItem>
                            </IonMenuToggle>
                        ))}
                    </IonList>
                </IonContent>
            </IonMenu>
            <ContextProvider key='ctxProv'>
                <IonRouterOutlet id="scheduleAppM1">
                    {/* <Route path='/all-activities' component={AllActivities} exact />
                    <Route path='/add-activity' component={AddActivity} exact /> */}
                    <Route path='/login' component={Login} exact />
                    <Route path='/alumnes' component={Alumnes} exact />
                    <Route path='/moduls' component={Moduls} exact />
                    <Route path='/logout' component={Login} exact />
                    <Redirect to={ context.logged ? '/moduls' : '/login' } />
                </IonRouterOutlet>
            </ContextProvider>
        </IonReactRouter>
    </IonApp>
)};

export default App;
